              <label for="bcc" class="col-2 col-sm-1 col-form-label">
                메일 템플릿
              </label>
              <div className="input-group-prepend"></div>
              <input
                type="text"
                className="form-control"
                placeholder="@"
                aria-label="mailTemplate"
                aria-describedby="basic-addon2"
              />
              <InputSideButton
                className="btn btn-primary ml-2"
                onClick={()=>{
                  setShowTemplate(!showTemplate);
                }}
              >
                템플릿
              </InputSideButton>
            </div>
            <div class="form-row mb-3">
              <label for="bcc" class="col-2 col-sm-1 col-form-label">
                제목
              </label>
              <div class="col-10 col-sm-11">
                <span>
                  <input
                    type="text"
                    class="form-control"
                    id="bcc"
                    placeholder="Title"
                  />
                </span>
              </div>
            </div>
            <div class="form-row mb-3">
              <label for="bcc" class="col-2 col-sm-1 col-form-label">
                파일 선택
              </label>
              <div class="col-10 col-sm-11">
                <FileInput type="file" class="form-control" />
              </div>
            </div>
          </form>
          <div class="row">
            <div class="col-sm-11 ml-auto">
              <div class="form-group mt-4" style={{ width: "100%" }}>
                <MailEditor width={mailState.tableWidth}/>
                <DivideHr />
                {showTemplate ? (
                  <ResultTemplateDiv>
                    <TemplateMailContents result={true} />
                  </ResultTemplateDiv>
                ) : null}
              </div>
            </div>
          </div>
        </main>
      </div>
    </SendMailDiv>
  );
}

const InputSideButton = styled.button`
  height: 38px;
`;
const DivideHr = styled.hr`
  background-color: #d1d3e2;
`;

const ResultTemplateDiv = styled.div`
  border: solid 2px #d1d3e2;
  border-radius: 5px;
  border-top: none;
`;

const FileInput = styled.input`
  border: none;
  button {
    border: none;
  }
`;
const SendMailDiv = styled.div`
  .email-app {
    display: flex;
    flex-direction: row;
    background: #fff;
    border: 1px solid #e1e6ef;
  }

  .email-app nav {
    flex: 0 0 200px;
    padding: 1rem;
    border-right: 1px solid #e1e6ef;
  }

  .email-app nav .btn-block {
    margin-bottom: 15px;
  }

  .email-app nav .nav {
    flex-direction: column;
  }

  .email-app nav .nav .nav-item {
    position: relative;
  }

  .email-app nav .nav .nav-item .nav-link,
  .email-app nav .nav .nav-item .navbar .dropdown-toggle,
  .navbar .email-app nav .nav .nav-item .dropdown-toggle {
    color: #151b1e;
    border-bottom: 1px solid #e1e6ef;
  }

  .email-app nav .nav .nav-item .nav-link i,
  .email-app nav .nav .nav-item .navbar .dropdown-toggle i,
  .navbar .email-app nav .nav .nav-item .dropdown-toggle i {
    width: 20px;
    margin: 0 10px 0 0;
    font-size: 14px;
    text-align: center;
  }

  .email-app nav .nav .nav-item .nav-link .badge,
  .email-app nav .nav .nav-item .navbar .dropdown-toggle .badge,
  .navbar .email-app nav .nav .nav-item .dropdown-toggle .badge {
    float: right;
    margin-top: 4px;
    margin-left: 10px;
  }

  .email-app main {
    min-width: 0;
    flex: 1;
    padding: 1rem;
  }

  .email-app .inbox .toolbar {
    padding-bottom: 1rem;
    border-bottom: 1px solid #e1e6ef;
  }

  .email-app .inbox .messages {
    padding: 0;
    list-style: none;
  }

  .email-app .inbox .message {
    position: relative;
    padding: 1rem 1rem 1rem 2rem;
    cursor: pointer;
    border-bottom: 1px solid #e1e6ef;
  }

  .email-app .inbox .message:hover {
    background: #f9f9fa;
  }

  .email-app .inbox .message .actions {
    position: absolute;
    left: 0;
    display: flex;
    flex-direction: column;
  }

  .email-app .inbox .message .actions .action {
    width: 2rem;
    margin-bottom: 0.5rem;
    color: #c0cadd;
    text-align: center;
  }

  .email-app .inbox .message a {
    color: #000;
  }

  .email-app .inbox .message a:hover {
    text-decoration: none;
  }

  .email-app .inbox .message.unread .header,
  .email-app .inbox .message.unread .title {
    font-weight: bold;
  }

  .email-app .inbox .message .header {
    display: flex;
    flex-direction: row;
    margin-bottom: 0.5rem;
  }

  .email-app .inbox .message .header .date {
    margin-left: auto;
  }

  .email-app .inbox .message .title {
    margin-bottom: 0.5rem;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .email-app .inbox .message .description {
    font-size: 12px;
  }

  .email-app .message .toolbar {
    padding-bottom: 1rem;
    border-bottom: 1px solid #e1e6ef;
  }

  .email-app .message .details .title {
    padding: 1rem 0;
    font-weight: bold;
  }

  .email-app .message .details .header {
    display: flex;
    padding: 1rem 0;
    margin: 1rem 0;
    border-top: 1px solid #e1e6ef;
    border-bottom: 1px solid #e1e6ef;
  }

  .email-app .message .details .header .avatar {
    width: 40px;
    height: 40px;
    margin-right: 1rem;
  }

  .email-app .message .details .header .from {
    font-size: 12px;
    color: #9faecb;
    align-self: center;
  }

  .email-app .message .details .header .from span {
    display: block;
    font-weight: bold;
  }

  .email-app .message .details .header .date {
    margin-left: auto;
  }

  .email-app .message .details .attachments {
    padding: 1rem 0;
    margin-bottom: 1rem;
    border-top: 3px solid #f9f9fa;
    border-bottom: 3px solid #f9f9fa;
  }

  .email-app .message .details .attachments .attachment {
    display: flex;
    margin: 0.5rem 0;
    font-size: 12px;
    align-self: center;
  }

  .email-app .message .details .attachments .attachment .badge {
    margin: 0 0.5rem;
    line-height: inherit;
  }

  .email-app .message .details .attachments .attachment .menu {
    margin-left: auto;
  }

  .email-app .message .details .attachments .attachment .menu a {
    padding: 0 0.5rem;
    font-size: 14px;
    color: #e1e6ef;
  }

  @media (max-width: 767px) {
    .email-app {
      flex-direction: column;
    }
    .email-app nav {
      flex: 0 0 100%;
    }
  }

  @media (max-width: 575px) {
    .email-app .message .header {
      flex-flow: row wrap;
    }
    .email-app .message .header .date {
      flex: 0 0 100%;
    }
  }
`;
